###############################################################################        Useful functions
#* In
def wbtc_In_filter(x):
    new_x = format(x, '.8f')
    new_x = new_x.replace('.', '')
    return int(new_x)
def amount_In_filter(web3, x, token):
    if token == 'WBTC':
        return wbtc_In_filter(x)
    elif token == 'USDC'or token == 'USDT':
        return web3.toWei(x, 'mwei')
    elif token == 'RLC':
        return web3.fromWei(x, 'gwei')
    else:
        return web3.toWei(x, 'ether')
#* Out
def wbtc_Out_filter(x, no_decimals):
    x_len = len(str(x))
    if x_len < 9:
        new_x = str(x)
        for i in range(8 - x_len):
            new_x = '0' + new_x
        new_x = '0.' + new_x
        return round(float(new_x), no_decimals)
    else:
        x0 = str(x)
        x_int = x0[0:(len(x0) - 8)]
        x_decimals = x0[(len(x0) - 8):(len(x0) - 1)]
        new_x = x_int + '.' + x_decimals
        return round(float(new_x), no_decimals)
def amount_Out_filter(web3, x, token, no_decimals = 4):
    if token == 'WBTC':
        return wbtc_Out_filter(x, no_decimals)
    elif token == 'USDC'or token == 'USDT':
        return round(float(web3.fromWei(x, 'mwei')), no_decimals)
    elif token == 'RLC':
        return round(float(web3.fromWei(x, 'gwei')), no_decimals)
    else:
        return round(float(web3.fromWei(x, 'ether')), no_decimals)
